import React, { Fragment } from "react";
import styles from "./Model.module.css";
import ReactDOM from "react-dom/client";
import { createPortal } from 'react-dom';
import Controller from "../layout/Controller";
import stylesLayout from "./layout.modules.css"


const BackDrop = ({ close ,show}) => {

  return <div className={`${styles.backDrop} ${show ? styles.showBackDrop : null}`} onClick={close}>



  </div>;
};

const OverLay = ({show}) => {
  return <div className={`${styles.overLay} ${show ? styles.showOverLay : ""}`} >

    <form>
      <Controller>
        <label htmlFor="name" className={stylesLayout.control_label}>Name</label>
        <input type="text" placeholder="enter name"></input>
        </Controller>
    </form>
  </div>;
};
const Model = ({ show, closeModel}) => {
  return (
    ( <Fragment>
      {/* <BackDrop></BackDrop>
            <OverLay></OverLay> */}
      {createPortal(
        <Fragment>
          <BackDrop close={closeModel} show={ show}></BackDrop>
          <OverLay show={ show}></OverLay>
        </Fragment>,
        document.getElementById("modal")
      )}
    </Fragment>)
  );
};

export default Model;
